import Head from 'next/head';
import React, { Component } from 'react';
import ReactTable from "react-table";
import styled from 'styled-components';
import "react-table/react-table.css";
import window from 'global'

import MonthPickerInput from 'react-month-picker-input';
import Loader from "./loader";
//import 'react-month-picker-input/dist/react-month-picker-input.css';
//var MonthPickerInput = require('react-month-picker-input');
require('react-month-picker-input/dist/react-month-picker-input.css');

import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Grid from '@material-ui/core/Grid';
const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    fullWidth: true,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));


const liff = window.liff;
const BOX = styled.div`
  margin: 5.5em auto;
`;

var leaves;


export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data : null,
      data_origin : null,
      role : "substitute",
      status: "not signed"
    };
  }

  componentDidMount() {
    this.liffInit();
  }

  liffInit() {
    try {
      liff.init(data => {
        const userId = data.context.userId;
        const accessToken = liff.getAccessToken();
        const URL_CHECKEXIST = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/checkExist";
        const body = {
          userId: userId
        };
        fetch(URL_CHECKEXIST, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.text();
        }).then(result => {
          if (result) {
              this.lineAuth(userId, accessToken);
          } else {
              alert("請登入以查看假單");
              window.location.replace("https://teddybear-dev.firebaseapp.com/signIn");
          }
        })
      }, err => {
        alert("error");
      });
    } catch (err) {
      throw(err);
    }
  }

  lineAuth (userId, accessToken)  {
    try {
      const url = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/lineAuth";
      const body = {
          userId: userId,
          accessToken: accessToken
        };
      fetch(url, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.json();
        }).then(result => {
          if (result) {
              const ubn = result.staff.ubn;
              const email = result.staff.email;
              const name = result.staff.name;
              const idToken = result.token.idToken;
              //this.getLeaves(ubn, email, idToken);
              //this.getLeavesSub(ubn, name, email, idToken);
              //this.getLeavesSuper(ubn, email, idToken);
              this.setState({
                ubn: ubn,
                name: name,
                email: email,
                idToken: idToken
              })
              if (this.state.role == "substitute") {
                this.getLeavesSub(this.state.ubn, this.state.name, this.state.email, this.state.idToken);
              } else if (this.state.role == "supervisor") {
                this.getLeavesSuper(this.state.ubn, this.state.email, this.state.idToken);
              }
          } else {
              alert("lineAuth no result");

          }
        }).catch(function (error) {
          alert(error.message);
        })
      } catch (err) {
        throw(err);
      }
  }

  getLeavesSub (ubn, name, email, idToken) {
    try {
      this.setState({ loading: true });
      const URL_GETLEAVES = "https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/get";
      const url = URL_GETLEAVES + `?ubn=${ubn}&query=substitute&value={"name":"${name}","email":"${email}"}`;
      const header = {
          Authorization: idToken
        };
      fetch(url, {
          method: 'get',
          headers: header
      }).then((res) => {
          return res.json();
      }).then(result => {
          if (result) {
            //alert("getLeavesSub success");
            this.convertData(result.list);
      } else {
          alert("getLeavesSub no result");
      }
      }).catch(function (error) {
          alert(error.message);
      })
    } catch (err) {
      throw(err);
    }
  }

  getLeavesSuper (ubn, email, idToken) {
    try {
      this.setState({ loading: true });
      const URL_GETLEAVES = "https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/get";
      const url = URL_GETLEAVES + `?ubn=${ubn}&query=supervisor&value=${email}`;
      const header = {
          Authorization: idToken
        };
      fetch(url, {
          method: 'get',
          headers: header
      }).then((res) => {
          return res.json();
      }).then(result => {
          if (result) {
            //alert("getLeavesSuper success");
            this.convertData(result.list);
      } else {
          alert("getLeavesSuper no result");
      }
      }).catch(function (error) {
          alert(error.message);
      })
    } catch (err) {
      throw(err);
    }
  }

  convertData (list)  {
    try {
      var newList = [];
      for (var i = 0; i < list.length; i++) {
        const data = {
          "startDate": list[i].startDate,
          "endDate": list[i].endDate,
          "leaveType": list[i].leaveType,
          "substitute": JSON.parse(list[i].substitute).name,
          "status": list[i].status,
          "leaveReason": list[i].leaveReason,
          "rejectReason": list[i].rejectReason
        };
        newList.push(data);
      }
      this.setState({
        data_origin: newList,
        loading: false 
      })
      this.globalSearch();
    } catch (err) {
      throw(err);
    }
  }

  handleRoleChange = event => {
    try{
      this.setState({ role: event.target.value }, () => {
        //console.log(event.target.value);
        //this.globalSearch();
        if (event.target.value == "substitute") {
          this.getLeavesSub(this.state.ubn, this.state.name, this.state.email, this.state.idToken);
        } else if (event.target.value == "supervisor") {
          this.getLeavesSuper(this.state.ubn, this.state.email, this.state.idToken);
        }
      });
    } catch (err) {
      throw(err);
    }
  };

  handleStatusChange = event => {
    try{
      this.setState({ status: event.target.value }, () => {
        //console.log(event.target.value);
        this.globalSearch();
      });
    } catch (err) {
      throw(err);
    }
  };

  globalSearch = () => {
    try {
      let role_select = this.state.role;
      let status_select = this.state.status;

      let filteredData = this.state.data_origin.filter(value => {
        if (role_select == "substitute") {
          switch (status_select) {
            case "signed":
              return (  
                value.status != "等待簽核"    
              );
            case "not signed":
              return (  
                value.status == "等待簽核"    
              );
            default:
              throw 'Option not supported';
          }
        } else if (role_select == "supervisor") {
          switch (status_select) {
            case "signed":
              return (  
                value.status == "同意 (主管)" || value.status == "拒絕 (主管)"    
              );
            case "not signed":
              return (  
                value.status == "同意 (代理人)"   
              );
            default:
              throw 'Option not supported';
          }
        }
      });
      this.setState({ data: filteredData, loading: false });
    } catch (err) {
      throw(err);
    }
  };

  render() {
    const MyLoader = () => <Loader loading={this.state.loading} />;
    const columns = [{
      Header: '開始日期',
      accessor: 'startDate',
      width: 90
    },{
      Header: '結束日期',
      accessor: 'endDate',
      width: 90
    },{
      Header: '假勤類別',
      accessor: 'leaveType',
      width: 90
    },{
      Header: '代理人',
      accessor: 'substitute',
      show: false
    },{
      Header: '簽核狀態',
      accessor: 'status',
      width: 90
    },{
      Header: '請假原因',
      accessor: 'leaveReason',
      show: false
    },{
      Header: '拒絕原因',
      accessor: 'rejectReason',
      show: false
    }];
    const classes = useStyles;

    return (
      <div>
        <Grid container spacing={1}>
        <Head>
          <script src="https://d.line-scdn.net/liff/1.0/sdk.js"></script>
        </Head>
        <Grid item xs={12}>
        <FormControl margin="normal" style={{minWidth: 150}} >
          <InputLabel id="role-label">簽核身份</InputLabel>
          <Select
            labelId="role-label"
            id="role"
            value={this.state.role}
            onChange={this.handleRoleChange}
          >
            <MenuItem value={"substitute"}>代理人</MenuItem>
            <MenuItem value={"supervisor"}>主管</MenuItem>
          </Select>
        </FormControl>
        </Grid>

        <Grid item xs={12}>
        <FormControl margin="normal" style={{minWidth: 150}}>
          <InputLabel id="status-label">簽核狀態</InputLabel>
          <Select
            labelId="status-label"
            id="status"
            value={this.state.status}
            onChange={this.handleStatusChange}
          >
            <MenuItem value={"not signed"}>未簽核</MenuItem>
            <MenuItem value={"signed"}>已簽核</MenuItem>
          </Select>
        </FormControl>
        </Grid>

        <br></br>
        <br></br>

        <div>
          {this.state.data ?
            <ReactTable
              data={this.state.data}
              getTrProps={(state, rowInfo, column, instance) => ({
                onClick: e => alert(
              `開始日期：${rowInfo.row.startDate}\n結束日期：${rowInfo.row.endDate}\n假勤類別：${rowInfo.row.leaveType}\n代理人：${rowInfo.row.substitute}\n簽核狀態：${rowInfo.row.status}\n拒絕原因：${rowInfo.row.rejectReason}`)
              })}
              columns={columns}
              defaultPageSize = {10}
              pageSizeOptions = {[10, 15]}
            /> :
            <ReactTable
              LoadingComponent={MyLoader}
              data={leaves}
              columns={columns}
              defaultPageSize = {10}
              pageSizeOptions = {[10, 15]}
            />
          }
        </div>
        </Grid> 
      </div>    
    );
  }
}
