// Render Prop
import React, { Component } from 'react';
import styled from 'styled-components';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { TextField } from 'formik-material-ui';
import fetch from 'isomorphic-unfetch';
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Head from 'next/head';
import window from 'global';
import { Select } from 'material-ui-formik-components/Select';
import {
  DatePicker,
  MuiPickersUtilsProvider,
} from '@material-ui/pickers';
import LuxonUtils from '@date-io/luxon';
require( "react-datepicker/dist/react-datepicker.css");
var dateFormat = require('dateformat');

const liff = window.liff;
const BOX = styled.div`
  margin: 5.5em auto;
`;
const TITLE = styled.h2`
  color: #F8F9F9;
  font-family: Candara;
  text-align: center;
  margin-bottom:-2em;
`;
const CONTAINER = styled.div`
  background: #F7F9FA;
  height: auto;
  width: 90%;
  margin: 5em auto;
  box-shadow: 5px 5px 5px 0px rgba(0, 0, 0, 0.4);

  label {
    font-family: Candara;
    color: #1C2833;
    font-size: 1.2em;
    font-weight: 400;
  }
`;
const MYFORM = styled(Form)`
  text-align: center;
  padding-top: 2em;
  padding-bottom: 2em;
`;
const FIELD = styled(Field)`
  width: 80%;
  margin: auto;
`;
const BUTTON = styled(Button)`
  width: 80%;
  variant: contained;
`;
const TomatoButton = styled.button`
  color: tomato;
  border-color: tomato;
  width: 80%;
`;


export default class App extends Component {
    constructor(props) {
      super(props);

      this.state = {
        substitutes : [],
        userId : null
      };
    }

    componentDidMount() {
      this.liffInit();
    }

    getQuery() {
        let urlParams = new URLSearchParams(window.location.search);
        urlParams = urlParams.toString().split("&");
        const ubn = urlParams[0].split("=")[1];
        const leaveId = urlParams[1].split("=")[1];
        //$('#demo').text(leaveId);
      }

    liffInit() {
      try {
        liff.init(data => {
          const userId = data.context.userId;
          const accessToken = liff.getAccessToken();
          const URL_CHECKEXIST = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/checkExist";
          const body = {
            userId: userId
          };
          fetch(URL_CHECKEXIST, {
            method: 'post',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(body)
          }).then((res) => {
            return res.text();
          }).then(result => {
            if (result) {
                this.setState({
                  userId: userId
              })
              this.lineAuth(userId, accessToken);
            } else {
                alert("請登入以查看假單");
                window.location.replace("https://teddybear-dev.firebaseapp.com/signIn");
            }
          })
        }, err => {
          alert(err.message);
        });
      } catch (err) {
        next(err);
      }
    }

    lineAuth (userId, accessToken)  {
      try {
        const url = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/lineAuth";
        const body = {
            userId: userId,
            accessToken: accessToken
          };
        fetch(url, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.json();
        }).then(result => {
          if (result) {
            const ubn = result.staff.ubn;
            //const department = result.staff.department;
            //const email = result.staff.email;
            const idToken = result.token.idToken;
            this.setState({
              Authorization: idToken
            })
            //this.getSubstitutes(ubn, department, idToken);
          } else {
              alert("lineAuth no result");
          }
        }).catch(function (error) {
          alert(error.message);
        })
      } catch (err) {
        next(err);
      }
    }

    updateLeave (values) {
      try {
        const URL_UPDATELEAVE = "https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/update";
        fetch(URL_UPDATELEAVE, {
          method: 'post',
          headers: {
            "Authorization": this.state.Authorization
          },
          body: JSON.stringify(values)
        }).then((res) => {
          return res.text();
        }).then(result => {
          if (result) {
            liff.closeWindow();
          } else {
              alert("updateLeave error");
          }
        }).catch(function (error) {
          alert(error.message);
        })
      } catch (err) {
        next(err);
      }
    }

    render() {
      return (
        <BOX>
          <Head>
            <script src="https://d.line-scdn.net/liff/1.0/sdk.js"></script>
          </Head>
          <body style={{background:'#34495E'}}>
            <BOX>
              <Formik
                initialValues={{ reason: '' }}
                validate={values => {
                  const errors = {};
                  // check for reason
                  if (!values.reason) {
                      errors.reason = 'Required';
                  }

                  return errors;

                }}
                onSubmit={(values, { setSubmitting }) => {
                  values.status = "拒絕 (代理人)";
                  this.submitForm(values);
                    setSubmitting(false);
                  //console.log('submitting', values);
                }}
              >
              {({ isSubmitting, values, setFieldValue}) => (
                <CONTAINER>
                  <MYFORM>
                    <FIELD 
                      type="reason" 
                      name="reason" 
                      label = "拒絕原因" 
                      component = { TextField }
                    />

                    <br></br>
                    <br></br>

                    <BUTTON
                      type="submit"
                      fontFamily= "Candara"
                      variant="contained"
                      color="secondary"
                      disabled={isSubmitting}
                    >
                    送出
                    </BUTTON>

                  </MYFORM>
                </CONTAINER>
              )}
              </Formik>
            </BOX>
          </body>
        </BOX>   
      );
    }
  }
