// Render Prop
import React, { Component, useState } from 'react';
import styled from 'styled-components';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { TextField } from 'formik-material-ui';
import fetch from 'isomorphic-unfetch';
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import icon from '../components/image/icon.png';
import Head from 'next/head';
import window from 'global';
//import DatePicker from "react-datepicker";
import { Select } from 'material-ui-formik-components/Select'
require( "react-datepicker/dist/react-datepicker.css");

import {
  DatePicker,
  TimePicker,
  DateTimePicker,
  MuiPickersUtilsProvider,
} from '@material-ui/pickers';
import MomentUtils from '@date-io/moment';
import DateFnsUtils from '@date-io/date-fns';
import LuxonUtils from '@date-io/luxon';

const liff = window.liff;
const BOX = styled.div`
  margin: 5.5em auto;
`;
const TITLE = styled.h2`
  color: #F8F9F9;
  font-family: Candara;
  text-align: center;
  margin-bottom:-2em;
`;
const CONTAINER = styled.div`
  background: #F7F9FA;
  height: auto;
  width: 90%;
  margin: 5em auto;
  box-shadow: 5px 5px 5px 0px rgba(0, 0, 0, 0.4);

  label {
    font-family: Candara;
    color: #1C2833;
    font-size: 1.2em;
    font-weight: 400;
  }
`;
const MYFORM = styled(Form)`
  text-align: center;
  padding-top: 2em;
  padding-bottom: 2em;
`;
const FIELD = styled(Field)`
  width: 80%;
  margin: auto;
`;
const BUTTON = styled(Button)`
  width: 80%;
  variant: contained;
`;
const TomatoButton = styled.button`
  color: tomato;
  border-color: tomato;
  width: 80%;
`;


export default class App extends Component {
    constructor(props) {
      super(props);

      this.state = {
        substitutes : [],
        userId : null
      };
    }

    componentDidMount() {
      this.liffInit();
    }

    liffInit() {
      liff.init(data => {
        const userId = data.context.userId;
        const accessToken = liff.getAccessToken();
        const URL_CHECKEXIST = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/checkExist";
        const body = {
          userId: userId
        };
        fetch(URL_CHECKEXIST, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.text();
        }).then(result => {
          if (result) {
              this.setState({
                userId: userId
            })
            this.lineAuth(userId, accessToken);
          } else {
              alert("請登入以查看假單");
              window.location.replace("https://teddybear-dev.firebaseapp.com/signIn");
          }
        })


      }, err => {
        alert("error");
      });
    }

    lineAuth (userId, accessToken)  {
      const url = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/lineAuth";
      const body = {
          userId: userId,
          accessToken: accessToken
        };
      fetch(url, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.json();
        }).then(result => {
          if (result) {
            const ubn = result.staff.ubn;
            const department = result.staff.department;
            const email = result.staff.email;
            const idToken = result.token.idToken;
            this.setState({
              Authorization: idToken,
              ubn: ubn,
              email: email
          })
            this.getSubstitutes(ubn, department, idToken);
          } else {
              alert("lineAuth no result");

          }
        }).catch(function (error) {
          alert(error.message);
        })
    }

    getSubstitutes (ubn, department, idToken) {
        const URL_GETSUBSTITUTES = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/getSubstitutes";
        const url = URL_GETSUBSTITUTES + `?ubn=${ubn}&query=department&value=${department}`; 
        const header = {
            Authorization: idToken
          };
        //document.write(idToken);
        fetch(url, {
            method: 'get',
            headers: header
          }).then((res) => {
            return res.json();
          }).then(result => {
            if (result) {
              this.createOptions(result.list)
            } else {
                alert("getSubstitutes no result");

            }
          }).catch(function (error) {
            alert(error.message);
          })
      }

    createOptions (arr) {
    var newList = [];
    for (var i = 0; i < arr.length; i++) {
        var obj = arr[i];
        obj.value = JSON.stringify({
          "name": obj.name,
          "email": obj.email
        });
        obj.label = obj.name;
        delete obj.email;
        delete obj.name;
        //push to list
        newList.push(obj);

        if(i == arr.length-1) {
          this.setState({
              substitutes: newList
          })
        }
        }
    }
    // tell Basic that options have been changed
    //substitutes = newList;

    submitForm (values) {
      alert("submitForm");
      const URL_CREATELEAVE = "https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/create";
      fetch(URL_CREATELEAVE, {
        method: 'post',
        headers: {
          "Authorization": this.state.Authorization,
          "ubn": this.state.ubn,
          "userId": this.state.userId,
          "Content-Type": "application/json"
        },
        body: JSON.stringify(values)
      }).then((res) => {
        alert(res);
        return res.text();
      }).then(result => {
        if (result) {
          alert(result);
          liff.closeWindow();
        } else {
            alert("submitForm error");
            //window.location.replace("https://teddybear-dev.firebaseapp.com/signIn");
        }
      }).catch(function (error) {
        alert(error.message);
      })
    }




    render() {
      return (
        <BOX>
    <Head>
      <script src="https://d.line-scdn.net/liff/1.0/sdk.js"></script>
    </Head>
    <body style={{background:'#34495E'}}>
      <BOX>
        <Formik
          initialValues={{ startDate: '', endDate: '', leaveType: '', leaveReason: '', substitute: '' }}
          validate={values => {
            const errors = {};
            // check for startDate
            if (!values.startDate) {
              errors.startDate = 'Required';
            }
            // check for endDate
            if (!values.endDate) {
              errors.endDate = 'Required';
            } 
            // check for leaveType
            if (!values.leaveType) {
              errors.leaveType = 'Required';
            }
            // check for leaveReason
            if (!values.leaveReason) {
                errors.leaveReason = 'Required';
            }
            /*
            // check for substitute
            if (!values.substitute) {
                errors.substitute = 'Required';
            }
            return errors;
            */
          }}
          onSubmit={(values, { setSubmitting }) => {
            values.email = this.state.email;
            alert(values);
            this.submitForm(values);
              setSubmitting(false);

            /*
            setTimeout(() => {
              this.submitForm(values);
              setSubmitting(false);
            }, 400);
            */

            console.log('submitting', values);
            //setSubmitting(false);
          }}
        >
          {({ isSubmitting, values, setFieldValue}) => (
            <CONTAINER>
            <MYFORM>
            <MuiPickersUtilsProvider utils={LuxonUtils}>
              <DatePicker value={new Date()} onChange={date => {
                console.log(date)

							}} />
            </MuiPickersUtilsProvider>

              <FIELD
                type="leaveType"
                name="leaveType"
                label="假勤類別"
                options={[
                  { value: '病假', label: '病假' },
                  { value: '事假', label: '事假' },
                  { value: '喪假', label: '喪假' },
                  { value: '公假', label: '公假' },
                  { value: '生理假', label: '生理假' },
                  { value: '婚假', label: '婚假' },
                  { value: '產假', label: '產假' },
                  { value: '陪產假', label: '陪產假' }
                ]}
                component={Select}
              />
              <FIELD type="leaveReason" name="leaveReason" label = "假勤事由" component = { TextField }/>
              <FIELD
                type="substitute"
                name="substitute"
                label="職務代理人"
                options={this.state.substitutes}
                component={Select}
              />
              <br></br>
              <br></br>
              <BUTTON
              type="submit"
              fontFamily= "Candara"
              variant="contained"
              color="secondary"
              disabled={isSubmitting}
            >
              送出假單
            </BUTTON>
            </MYFORM>
            </CONTAINER>
          )}
        </Formik>
      </BOX>
    </body>
    </BOX>   
      );
    }
  }
