import Head from 'next/head';
import React, { Component } from 'react';
import ReactTable from "react-table";
import styled from 'styled-components';
import "react-table/react-table.css";
import window from 'global'

import MonthPickerInput from 'react-month-picker-input';
import Loader from "./loader";
//import 'react-month-picker-input/dist/react-month-picker-input.css';
//var MonthPickerInput = require('react-month-picker-input');
require('react-month-picker-input/dist/react-month-picker-input.css');

import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));


const liff = window.liff;
const BOX = styled.div`
  margin: 5.5em auto;
`;

var leaves;


export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data : null
    };
  }

  componentDidMount() {
    this.liffInit();
  }

  liffInit() {
    try {
      liff.init(data => {
        const userId = data.context.userId;
        const accessToken = liff.getAccessToken();
        const URL_CHECKEXIST = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/checkExist";
        const body = {
          userId: userId
        };
        fetch(URL_CHECKEXIST, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.text();
        }).then(result => {
          if (result) {
              this.lineAuth(userId, accessToken);
          } else {
              alert("請登入以查看假單");
              window.location.replace("https://teddybear-dev.firebaseapp.com/signIn");
          }
        })
      }, err => {
        alert("error");
      });
    } catch (err) {
      throw(err);
    }
  }

  lineAuth (userId, accessToken)  {
    try {
      const url = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/lineAuth";
      const body = {
          userId: userId,
          accessToken: accessToken
        };
      fetch(url, {
          method: 'post',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        }).then((res) => {
          return res.json();
        }).then(result => {
          if (result) {
              const ubn = result.staff.ubn;
              const email = result.staff.email;
              const idToken = result.token.idToken;
              this.getLeaves(ubn, email, idToken);
          } else {
              alert("lineAuth no result");

          }
        }).catch(function (error) {
          alert(error.message);
        })
      } catch (err) {
        throw(err);
      }
  }

  getLeavesSub (ubn, name, email, idToken) {
    try {
      const URL_GETLEAVES = "https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/get";
      const url = URL_GETLEAVES + `?ubn=${ubn}&query=substitute&value={"name":"${name}","email":"${email}"}`;
      const header = {
          Authorization: idToken
        };
      fetch(url, {
          method: 'get',
          headers: header
      }).then((res) => {
          return res.json();
      }).then(result => {
          if (result) {
            this.convertData(result.list);
      } else {
          alert("getLeaves no result");
      }
      }).catch(function (error) {
          alert(error.message);
      })
    } catch (err) {
      throw(err);
    }
  }

  getLeavesSuper (ubn, email, idToken) {
    try {
      const URL_GETLEAVES = "https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/get";
      const url = URL_GETLEAVES + `?ubn=${ubn}&query=supervisor&value=${email}`;
      const header = {
          Authorization: idToken
        };
      fetch(url, {
          method: 'get',
          headers: header
      }).then((res) => {
          return res.json();
      }).then(result => {
          if (result) {
            this.convertData(result.list);
      } else {
          alert("getLeaves no result");
      }
      }).catch(function (error) {
          alert(error.message);
      })
    } catch (err) {
      throw(err);
    }
  }

  convertData (list)  {
    try {
      var newList = [];
      for (var i = 0; i < list.length; i++) {
        const data = {
          "startDate": list[i].startDate,
          "endDate": list[i].endDate,
          "leaveType": list[i].leaveType,
          "substitute": JSON.parse(list[i].substitute).name,
          "status": list[i].status,
          "leaveReason": list[i].leaveReason,
          "rejectReason": list[i].rejectReason
        };
        newList.push(data);
      }
      this.setState({
        data: newList,
        data_origin: newList
      })
    } catch (err) {
      throw(err);
    }
  }

  handleChange = event => {
    try{
      this.setState({ role: event }, () => {
        //this.globalSearch();
      });
    } catch (err) {
      throw(err);
    }
  };

  globalSearch = () => {
    try {
      let { role } = this.state;
      var date_array = role.split("/");
      var date_select = new Date(date_array[1], date_array[0]);
      var year_select = date_select.getFullYear();
      var month_select = date_select.getMonth();

      let filteredData = this.state.data_origin.filter(value => {
        var date = new Date(value.startDate);
        var year = date.getFullYear();
        var month = date.getMonth() + 1;
        return (      
          year === year_select &&
          month === month_select  
        );
      });
      this.setState({ data: filteredData, loading: false });
    } catch (err) {
      throw(err);
    }
  };

  render() {
    const MyLoader = () => <Loader loading={this.state.loading} />;
    let months = ['Jan', 'Feb', 'Mar', 'Spr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    let makeText = m => {
      if (m && m.year && m.month) return (months[m.month-1] + '. ' + m.year)
      return '?'
    };
    let mvalue = {year: 2015, month: 11};
    const columns = [{
      Header: '開始日期',
      accessor: 'startDate',
      id: "startDate",
    },{
      Header: '結束日期',
      accessor: 'endDate'
    },{
      Header: '假勤類別',
      accessor: 'leaveType'
    },{
      Header: '代理人',
      accessor: 'substitute'
    },{
      Header: '簽核狀態',
      accessor: 'status'
    },{
      Header: '請假原因',
      accessor: 'leaveReason',
      show: false
    },{
      Header: '拒絕原因',
      accessor: 'rejectReason',
      show: false
    }];
    const classes = useStyles;

    return (
      <div>
        <Head>
          <script src="https://d.line-scdn.net/liff/1.0/sdk.js"></script>
        </Head>
        <p>年/月</p>
        <MonthPickerInput 
          onChange={this.handleChange}
        />
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">簽核身份</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={this.state.role}
            onChange={this.handleChange}
          >
            <MenuItem value={"substitute"}>代理人</MenuItem>
            <MenuItem value={"supervisor"}>主管</MenuItem>
          </Select>
        </FormControl>
        <br></br>
        <br></br>
        <div>
          {this.state.data ?
            <ReactTable
              data={this.state.data}
              getTrProps={(state, rowInfo, column, instance) => ({
                onClick: e => alert(
              `開始日期：${rowInfo.row.startDate}\n結束日期：${rowInfo.row.endDate}\n假勤類別：${rowInfo.row.leaveType}\n代理人：${rowInfo.row.substitute}\n簽核狀態：${rowInfo.row.status}\n拒絕原因：${rowInfo.row.rejectReason}`)
              })}
              columns={columns}
              defaultPageSize = {10}
              pageSizeOptions = {[10, 15]}
            /> :
            <ReactTable
              LoadingComponent={MyLoader}
              data={leaves}
              columns={columns}
              defaultPageSize = {10}
              pageSizeOptions = {[10, 15]}
            />
          }
        </div> 
      </div>    
    );
  }
}
