// Render Prop
import React from 'react';
import styled from 'styled-components';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { TextField } from 'formik-material-ui';
import fetch from 'isomorphic-unfetch';
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import icon from '../components/image/icon.png';
import Head from 'next/head';
import window from 'global'
const liff = window.liff;
const BOX = styled.div`
  margin: 5.5em auto;
`;
const AppLogo = styled.img`
  width: 50%;
  display: block;
  margin-top: 2em;
  margin-left: auto;
  margin-right: auto;
`;
const TITLE = styled.h2`
  color: #F8F9F9;
  font-family: Candara;
  text-align: center;
  margin-bottom:-2em;
`;
const CONTAINER = styled.div`
  background: #F7F9FA;
  height: auto;
  width: 90%;
  margin: 5em auto;
  box-shadow: 5px 5px 5px 0px rgba(0, 0, 0, 0.4);

  label {
    font-family: Candara;
    color: #1C2833;
    font-size: 1.2em;
    font-weight: 400;
  }
`;
const MYFORM = styled(Form)`
  text-align: center;
  padding-top: 2em;
  padding-bottom: 2em;
`;
const FIELD = styled(Field)`
  width: 80%;
`;
const BUTTON = styled(Button)`
  width: 80%;
  variant: contained;
`;
const TomatoButton = styled.button`
  color: tomato;
  border-color: tomato;
  width: 80%;
`;

function submitForm (data, userId) {
  data.userId = userId;
  fetch('https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/signIn', {
    method: 'post',
    headers: {
      'Accept': 'application/json, text/plain, */*',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data)
  }).then((res) => {
    res.status === 200 ? this.setState({ submitted: true }) : ''
  })
}

const liffInit = async () => {
  liff.init(data => {
    // save userId
    const userId = data.context.userId;
    alert("success");
    alert(userId);

  }, err => {
    alert("error");
  });
}
liffInit();
const Basic = () => (
  <BOX>
  <Head>
    <script src="https://d.line-scdn.net/liff/1.0/sdk.js"></script>
  </Head>
  <body style={{background:'#34495E'}}>
    <BOX>
      <AppLogo src={icon}/>
      <TITLE>Welcome to Teddy Bear!</TITLE>
      <Formik
        initialValues={{ ubn: '', email: '', password: '' }}
        validate={values => {
          const errors = {};
          // check for ubn
          if (!values.ubn) {
            errors.ubn = 'Required';
          }
          // check for email
          if (!values.email) {
            errors.email = 'Required';
          } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
          ) {
            errors.email = 'Invalid email address';
          }
          // check for password
          if (!values.password) {
            errors.password = 'Required';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          /*
          setTimeout(() => {
            alert(JSON.stringify(values, null, 3));
            setSubmitting(false);
          }, 400);
          */
          console.log('submitting', values);
        }}
      >
        {({ isSubmitting }) => (
          <CONTAINER>
          <MYFORM>
            <FIELD type="ubn" name="ubn" label = "UBN" component = { TextField }/>
            <FIELD type="email" name="email" label = "Email" component = { TextField }/>
            <FIELD type="password" name="password" label = "Password" component = { TextField }/>
            <br></br>
            <br></br>
            <BUTTON
            type="submit"
            fontFamily= "Candara"
            variant="contained"
            color="secondary"
            disabled={isSubmitting}
          >
            Sign In
          </BUTTON>
          </MYFORM>
          </CONTAINER>
        )}
      </Formik>
    </BOX>
  </body>
  </BOX>

);


export default Basic;
