// Render Prop
import React, { Component } from 'react';
import styled from 'styled-components';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { TextField } from 'formik-material-ui';
import fetch from 'isomorphic-unfetch';
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Head from 'next/head';
import window from 'global';
import { Select } from 'material-ui-formik-components/Select';
import {
  DatePicker,
  MuiPickersUtilsProvider,
} from '@material-ui/pickers';
import LuxonUtils from '@date-io/luxon';
require( "react-datepicker/dist/react-datepicker.css");
var dateFormat = require('dateformat');

const liff = window.liff;
const BOX = styled.div`
  margin: 5.5em auto;
`;
const TITLE = styled.h2`
  color: #F8F9F9;
  font-family: Candara;
  text-align: center;
  margin-bottom:-2em;
`;
const CONTAINER = styled.div`
  background: #F7F9FA;
  height: auto;
  width: 90%;
  margin: 5em auto;
  box-shadow: 5px 5px 5px 0px rgba(0, 0, 0, 0.4);

  label {
    font-family: Candara;
    color: #1C2833;
    font-size: 1.2em;
    font-weight: 400;
  }
`;
const MYFORM = styled(Form)`
  text-align: center;
  padding-top: 2em;
  padding-bottom: 2em;
`;
const FIELD = styled(Field)`
  width: 80%;
  margin: auto;
`;
const BUTTON = styled(Button)`
  width: 80%;
  variant: contained;
`;
const TomatoButton = styled.button`
  color: tomato;
  border-color: tomato;
  width: 80%;
`;


export default class App extends Component {
    constructor(props) {
      super(props);

      this.state = {
        userId : null,
        Authorization: null,
        ubn: null,
        leaveId: null,
        leave: null
      };
    }

    componentDidMount() {
      this.getQuery();
      this.liffInit();
    }

    getQuery() {
        let urlParams = new URLSearchParams(window.location.search);
        urlParams = urlParams.toString().split("&");
        const ubn = urlParams[0].split("=")[1];
        const leaveId = urlParams[1].split("=")[1];
        this.setState({
            leaveId: leaveId
        })
    }

    getStaff (Authorization, ubn, email) {
        try {
          const URL_GETSTAFF = `https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/target?ubn=${ubn}&email=${email}`;
          fetch(URL_GETSTAFF, {
            method: 'get',
            headers: {
                "Authorization": Authorization
            }
          }).then((res) => {
            if(res.ok) {
                return res.json();  
            }
            throw new Error('getStaff response was not ok.');
          }).then(result => {
            const substituteInfo = JSON.parse(result.substitute);
            const messages_self = [{
                "type": "flex",
                "altText": "代理人已拒絕假單",
                "contents": {
                    "type": "bubble",
                    "styles": {

                    },
                    "body": {
                    "type": "box",
                    "layout": "vertical",
