    liffInit() {
      try {
        liff.init(data => {
            const userId = data.context.userId;
            const accessToken = liff.getAccessToken();
            const URL_CHECKEXIST = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/checkExist";
            const body = {
                userId: userId
            };
            fetch(URL_CHECKEXIST, {
                method: 'post',
                headers: {
                'Content-Type': 'application/json'
                },
                body: JSON.stringify(body)
            }).then((res) => {
                return res.text();
            }).then(result => {
                if (result) {
                    this.setState({
                        userId: userId
                    })
                    this.lineAuth(userId, accessToken);
                } else {
                    alert("請登入以查看假單");
                    window.location.replace("https://teddybear-dev.firebaseapp.com/signIn");
                }
            })
        }, err => {
            alert(err.message);
        });
      } catch (err) {
            next(err);
      }
    }

    lineAuth (userId, accessToken)  {
      try {
        const url = "https://us-central1-teddybear-dev.cloudfunctions.net/api/staff/lineAuth";
        const body = {
            userId: userId,
            accessToken: accessToken
        };
        fetch(url, {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(body)
        }).then((res) => {
            return res.json();
        }).then(result => {
            if (result) {
                const ubn = result.staff.ubn;
                //const department = result.staff.department;
                //const email = result.staff.email;
                const idToken = result.token.idToken;
                this.setState({
                    Authorization: idToken,
                    ubn: ubn
                })
                this.checkStatus(idToken, ubn, this.state.leaveId);
            } else {
                alert("lineAuth no result");
            }
        }).catch(function (error) {
            alert(error.message);
        })
      } catch (err) {
            next(err);
      }
    }

    checkStatus (Authorization, ubn, id) {
        try {
            const URL_GETLEAVE = `https://us-central1-teddybear-dev.cloudfunctions.net/api/leave/target?ubn=${ubn}&id=${id}`;
            fetch(URL_GETLEAVE, {
                method: 'get',
                headers: {
                    Authorization: Authorization
                }
            }).then((res) => {
                if(res.ok) {
                return res.json();  
                }
                throw new Error('checkStatus response was not ok.');
            }).then(result => {
                if (result.status != "等待簽核") {
                    alert("此張假單已簽核");
                    window.location.replace("https://teddybear-dev.firebaseapp.com/approveLeave/reject/alreadyApproved/");
                }
            }).catch(function (error) {
                alert(error.message);
            })
        } catch (err) {
            next(err);
        }
    }

